import viewsIcon from '../assets/images/Icons/views.svg';
import likesIcon from '../assets/images/Icons/likes.svg';
import formatDate from '../helpers/formatDate';

function MainVid(props){

    return(
        // video
        //video title

        //video author
        //video view
        //likes
        //date

        //desc
        <div className="video">
            <div className="video__nowPlaying">
                <video className="video__vidImg" poster={props.video.image} controls>

                </video>
            </div>
            <div className="video__title">
                <p>{props.video.title}</p>
            </div>
            <div className="video__info">
                <div className='video__details'>
                    <div className="video__author"> 
                        <p>{props.video.channel}</p>
                    </div>

                    <div className="video__view">
                        <img src={viewsIcon} alt="views icon" />
                        <p>{props.video.views}</p>
                    </div>
                </div>
                <div className='video__details'>
                    <div className="video__likes">
                        <img src={likesIcon} alt="likes icon"></img>
                        <p>{props.video.likes}</p>
                    </div>
                    <div className="video__date">
                        <p>{formatDate(props.video.timestamp)}</p>
                    </div>
                </div>
            </div>
            <div className="video__desc">
                <p>{props.video.description}</p>
            </div>
        </div>
    )
}

export default MainVid;
