import Header from './components/Header';
import Comment from './components/Comment';
import MainVid from './components/MainVid';
import NextVideo from './components/NextVideo';

// import data from json
import videoDetails from './data/video-details.json';
import videos from './data/videos.json';

//import images
import iconImg from './assets/images/Images/Mohan-muruge.jpg';

function App() {
  //comments

  //main video

  //video list

  //state function for handling vid switching

  //make state fx for main vid
  const [video, setVideo] = useState(videoDetails[0]);

  return (
    <div className="App">
      <Header />
      {/* gets the whole thing i think */}
      <MainVid video={video}/>
      {/* only gets comment data */}
      <Comment commentData = {video}/>

      <NextVideo video={setVideo}/>

    </div>
  );
}

export default App;
